using UnityEngine;using System.Collections;public class MiniBossOne : MonoBehaviour{    public AnimationCurve _moveEasing;    public float _moveSpeed = 1f;    public float _delayBetweenMoving;    public float _edgeMargin = 0.2f;    Vector3 _targetPosWorld;    float _timeFromLastMovement;    float _timeFromMovementStart;    bool _moving;    void Update()    {        if(Time.time - _delayBetweenMoving > _timeFromLastMovement && !_moving)        {            float x = Random.Range(0.5f + _edgeMargin, 1f - _edgeMargin);            float y = Random.Range(0f + _edgeMargin, 1f - _edgeMargin);            _targetPosWorld = Camera.main.ViewportToWorldPoint(new Vector3(x, y));            _targetPosWorld.z = 0;            _timeFromMovementStart = Time.time;            _moving = true;        }        if(_moving)        {            if((transform.position - _targetPosWorld).magnitude < 0.01f)            {                _moving = false;                _timeFromLastMovement = Time.time;            }            else            {                transform.position = Vector3.Lerp(transform.position, _targetPosWorld, _moveEasing.Evaluate((Time.time - _timeFromMovementStart) * _moveSpeed));            }        }    }}